import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
const ADDRESS = 'Islam Market, Boro Kalibari Road, Tangail';
const PHONE = '01316-282335, 01911-330133';
// Place your logo as a base64 string here (or load from file if needed)
const LOGO_BASE64 = ``; // TODO: Replace with your actual base64 logo string

// const STORE_NAME = 'chicken hut';
const FLASK_PRINT_URL = 'http://localhost:5000/print';
const API_KEY = 'your-secret-api-key';

export async function POST(req: NextRequest) {
  try {
    const { orderId, type } = await req.json();
    if (!orderId || !type) {
      return NextResponse.json(
        { error: 'orderId and type are required' },
        { status: 400 },
      );
    }
    if (type !== 'kitchen' && type !== 'customer') {
      return NextResponse.json(
        { error: 'type must be "kitchen" or "customer"' },
        { status: 400 },
      );
    }

    // Fetch order with items, menu items, and table
    const order = await prisma.order.findUnique({
      where: { id: orderId },
      include: {
        table: true,
        orderItems: {
          include: { menuItem: true },
        },
      },
    });
    if (!order) {
      return NextResponse.json({ error: 'Order not found' }, { status: 404 });
    }

    // Build content for print
    const content: Array<Record<string, unknown>> = [];

    if (type === 'kitchen') {
      // Kitchen order with LARGE FONT (using the special format for kitchen orders)
      content.push({ type: 'header', text: 'KITCHEN ORDER' });
      content.push({
        type: 'text',
        text: `Table: ${order.isParcel ? 'PARCEL' : order.table?.number || ''}`,
      });

      // Add time/date
      content.push({
        type: 'text',
        text: new Date(order.createdAt).toLocaleString(),
      });

      // Order items with item numbers - format as items with name and quantity
      // This will trigger the special kitchen print formatting in the Flask server
      for (const item of order.orderItems) {
        content.push({
          name: `item no.${item.menuItem.itemNumber} `,
          quantity: `= ${item.quantity} pics`,
        });
      }
    }
    // else if (type === 'customer') {
    //   // Logo, header, items, total, etc.
    //   if (LOGO_BASE64) {
    //     content.push({ type: 'image', data: LOGO_BASE64 });
    //   }
    //   content.push({ type: 'header', text: STORE_NAME });
    //   content.push({
    //     type: 'text',
    //     text: `Table: ${order.isParcel ? 'Parcel' : order.table?.number || ''}`,
    //   });

    //   content.push({
    //     type: 'text',
    //     text: new Date(order.createdAt).toLocaleString(),
    //   });

    //   // Add items with regular formatting
    //   for (const item of order.orderItems) {
    //     content.push({
    //       type: 'item',
    //       name: item.menuItem.name,
    //       quantity: item.quantity,
    //       price: item.price,
    //     });
    //   }

    //   content.push({ type: 'total', amount: order.total });
    //   if (order.discount && order.discount > 0) {
    //     content.push({
    //       type: 'text',
    //       text: `Discount: -${order.discount.toFixed(2)}`,
    //     });
    //   }
    // }
    else if (type === 'customer') {
      if (LOGO_BASE64) {
        content.push({ type: 'image', data: LOGO_BASE64 });
      }
      // Address and phone (left-aligned)
      content.push({ type: 'address', align: 'left', text: ADDRESS });
      content.push({ type: 'phone', align: 'left', text: PHONE });
      // Date/time at the top
      content.push({
        type: 'text',
        align: 'left',
        text: new Date(order.createdAt).toLocaleString(),
      });
      // content.push({ type: 'header', text: STORE_NAME });
      content.push({
        type: 'text',
        text: `Table: ${order.isParcel ? 'Parcel' : order.table?.number || ''}`,
      });

      // Table header
      content.push({
        type: 'table-header',
        columns: ['No', 'Item Name', 'Qty', 'Rate', 'Total'],
      });

      // Table rows
      
      for (const item of order.orderItems) {
        content.push({
          type: 'table-row',
          columns: [
            item.menuItem.itemNumber,
            item.menuItem.name,
            String(item.quantity),
            item.price.toFixed(2),
            (item.price * item.quantity).toFixed(2),
          ],
        });
      }

      // Discount row (if any)
      if (order.discount && order.discount > 0) {
        content.push({
          type: 'discount',
          label: 'Discount',
          amount: `-${order.discount.toFixed(2)}`,
        });
      }

      // Subtotal row
      content.push({
        type: 'subtotal',
        label: 'Total',
        amount: order.total.toFixed(2),
      });
    }

    // Send to Flask print server
    const printRes = await fetch(FLASK_PRINT_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-API-KEY': API_KEY,
      },
      body: JSON.stringify({
        content,
        print_type: type, // Pass the print_type to the Flask server
      }),
    });

    const printData = await printRes.json();
    if (!printRes.ok) {
      return NextResponse.json(
        { error: printData.error || 'Print failed' },
        { status: 500 },
      );
    }

    // Update order fields after successful print
    if (type === 'customer') {
      await prisma.order.update({
        where: { id: orderId },
        data: {
          paid: true,
          completedAt: new Date(),
          customerPrinted: true,
        },
      });
    } else if (type === 'kitchen') {
      await prisma.order.update({
        where: { id: orderId },
        data: {
          kitchenPrinted: true,
        },
      });
    }

    return NextResponse.json({
      success: true,
      message: printData.message || 'Printed successfully',
    });
  } catch (err: unknown) {
    function hasMessage(e: unknown): e is { message: string } {
      return (
        typeof e === 'object' &&
        e !== null &&
        'message' in e &&
        typeof (e as { message: unknown }).message === 'string'
      );
    }
    let message = 'Internal server error';
    if (hasMessage(err)) {
      message = err.message;
    }
    return NextResponse.json({ error: message }, { status: 500 });
  }
}
